import React, {useState} from "react";
import { useSelector } from 'react-redux';
import { Link } from "react-router-dom";



function SalePersonTable() {
    var productIndex = 0;
    const saleperson = useSelector((state) => state.salesperson);
    console.log(saleperson);


	let salRow = saleperson.map((sal) => {
		var rowColor;
		var startdate = sal.startdate.toString();
		console.log(startdate);
		//startdate= startdate.slice(8);

		if (productIndex++ % 2 === 0) {
			rowColor = "dark:bg-stone-700";
		} else {
			rowColor = "dark:bg-stone-600";
		}
		return (
			<tr className={`bg-white ${rowColor} `}>
				<th
					scope="row"
					className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap"
				>
					{sal.fname}
				</th>
				<th scope ="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{sal.lname}
				</th>
				<th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{sal.address}
				</th>
				<th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{sal.phone}
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{sal.startdate}
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{sal.terminationdate}
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{sal.manager}
				</th>
				<td className="px-2 py-4 whitespace-nowrap">
						<button
                            data-bs-toggle='modal'
                            data-modal-toggle='authentication-modal'
							className="flex-right px-3 py-2 h-full bg-green-600 hover:bg-green-700 text-white transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none rounded-lg "
						>
							Edit
						</button>
				</td>
			</tr>
		);
	});

	return (
		<div className="mt-10 relative  shadow-md sm:rounded-lg h-full overflow-visible">
			<table className="w-full text-sm text-center text-gray-500 dark:text-gray-50">
				<thead className="text-xs text-white-700 uppercase bg-white-50 dark:bg-white-700 dark:text-white-400 dark:bg-stone-800">
					<tr>
						<th scope="col" className="px-2 py-4">
							First Name
						</th>
						<th scope="col" className="px-2 py-4">
							Last Name
						</th>
						<th scope="col" className="px-2 py-4">
							Address
						</th>
						<th scope="col" className="px-2 py-4">
							Phone
						</th>
						<th scope="col" className="px-2 py-4">
							Start Date
						</th>
                        <th scope="col" className="px-2 py-4">
							Termination Date
						</th>
                        <th scope="col" className="px-2 py-4">
							Manager
						</th>
                        <th scope="col" className="px-2 py-4">
							Options
						</th>
					</tr>
				</thead>
				<tbody>{salRow}</tbody>
			</table>
		</div>
	);
}
export default SalePersonTable;
