import * as api from '../api';

export const getSalesPerson = () => async (salespersonDispatch) => {
    try {
        const {data} = await api.fetchSalesPersons();
        salespersonDispatch({type: 'FETCH_ALL', payload: data});
    } catch (error) {
        console.log(error.message);
    }
    
}
