import React from "react";
import ProdcutTable from "../components/producttable";
import ProModalEdit from "../components/editproductmodal";
import { useSelector } from "react-redux";
import {Link, useHistory } from "react-router-dom";



function Product({currentId, setCurrentId}) {

    function handleAdd() {
        window.location.replace('/addproduct')       
    }
    const products = useSelector((state) => state.products);
    let editModal = products.map((pro) => {
        return(
            <React.Fragment>
                <ProModalEdit
                    key={pro._id}
                    productId={pro._id}
                    name={pro.name}
                    manufacturer={pro.manufacturer}
                    style={pro.style}
                    purchasePrice={pro.purchasePrice}
                    salePrice={pro.salePrice}
                    quantity={pro.quantity}
                    commisionPercentage={pro.commisionPercentage}
                    />
            </React.Fragment>
        )
    })

    return(
        <div className="h-full w-screen flex justify-center">
            <div className="flex-col w-8/12 h-full">
                <ProdcutTable setCurrentId={setCurrentId}/>
            </div> 
            {editModal}
        </div>
    )
}

export default Product;
