import React, { useState, useEffect } from "react";
import { useSelector } from 'react-redux';
import { useDispatch } from "react-redux";
import { getProducts} from './actions/products';

function ProdcutTable() {
    var productIndex = 0;

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(getProducts());
    }, [dispatch]);

    const products = useSelector((state) => state.products);
    console.log(products);

	let proRow = products.map((pro) => {
		var rowColor;

		if (productIndex++ % 2 == 0) {
			rowColor = "dark:bg-stone-700";
		} else {
			rowColor = "dark:bg-stone-600";
		}
		return (
			<tr className={`bg-white ${rowColor} `}>
				<th
					scope="row"
					className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap"
				>
					{pro.Name}
				</th>
				<th scope ="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					Manufacturer
				</th>
				<th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					Style
				</th>
				<th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					Purchase Price
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					Sale Price
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					Quantity
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					Commision Percent
				</th>
				<td className="px-2 py-4 whitespace-nowrap">
					<div className="grid grid-cols-2 content-center">
						<button
							data-bs-toggle="modal"
							data-bs-target={`#editModal`}
							className="flex-right px-3 py-2 justify-self-center h-full bg-green-600 hover:bg-green-700 text-white transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none rounded-lg "
						>
							Edit
						</button>
						<button
							data-bs-toggle="modal"
							data-bs-target={`#deleteModal`}
							className="px-1 py-2 justify-self-center h-full whitespace-nowrap bg-red-600 hover:bg-red-700 text-white transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none rounded-lg "
						>
							Delete
						</button>
						
					</div>
				</td>
			</tr>
		);
	});

	return (
		<div className="mt-10 relative  shadow-md sm:rounded-lg h-full overflow-visible">
			<table className="w-full text-sm text-center text-gray-500 dark:text-gray-50">
				<thead className="text-xs text-white-700 uppercase bg-white-50 dark:bg-white-700 dark:text-white-400 dark:bg-stone-800">
					<tr>
						<th scope="col" className="px-2 py-4">
							Name
						</th>
						<th scope="col" className="px-2 py-4">
							Manufacturer
						</th>
						<th scope="col" className="px-2 py-4">
							Style
						</th>
						<th scope="col" className="px-2 py-4">
							Purchase Price
						</th>
						<th scope="col" className="px-2 py-4">
							Purchase Price
						</th>
						<th scope="col" className="px-2 py-4">
							Sale Price
						</th>
                        <th scope="col" className="px-2 py-4">
							Quantity
						</th>
                        <th scope="col" className="px-2 py-4">
							Commision Percent
						</th>
					</tr>
				</thead>
				<tbody>{proRow}</tbody>
			</table>
		</div>
	);
}
export default ProdcutTable;
