import axios from "axios";
import React, { useState, useEffect } from "react";

function NewSale() {
    var productName;
    var salesPerson;
    const [product, setProduct] = useState([]);
    

    const getProduct = async () => {
		try {
			const response = await axios.get('/products');
			const jsonData = await response.data;

			console.log(jsonData);
			setProduct(jsonData);
		}catch(err){
			console.error(err.message);
		}
	}

	useEffect(() => {
		getProduct();
	}, []);
    let prod = product.map((pro) => {
        return(
            <option value={pro.name}>{pro.name}</option>
        )
    })

    const [salesperson, setSalesPerson] = useState([]);

	const getSalesPerson = async () => {
		try {
			const response = await axios.get('/salespersons');
			const jsonData = await response.data;

			console.log(jsonData);
			setSalesPerson(jsonData);
		}catch(err){
			console.error(err.message);
		}
	}

	useEffect(() => {
		getSalesPerson();
	}, []);

    let sale = salesperson.map((sal) => {
        return(
            <option value={[sal.lname, sal.fname]}>{sal.lname}, {sal.fname}</option>
        )
    })
    const [customer, setCustomer] = useState([]);

	const getCustomer = async () => {
		try {
			const response = await axios.get('/customers');
			const jsonData = await response.data;

			console.log(jsonData);
			setCustomer(jsonData);
		}catch(err){
			console.error(err.message);
		}
	}

	useEffect(() => {
		getCustomer();
	}, []);

    let cust = customer.map((cus) => {
        return(
            <option value={[cus.lname, cus.fname]}>{cus.lname}, {cus.fname}</option>
        )
    })
    return (
        <div className="text-white py-4 w-8/12 px-4">
            <div class="mb-6">
                <div class="">
                    <div class="mb-3 xl:w-70">
                        Product
                        <select class="form-select appearance-none
                                    block
                                    w-full
                                    px-3
                                    py-1.5
                                    text-base
                                    font-normal
                                    text-gray-700
                                    bg-white bg-clip-padding bg-no-repeat
                                    border border-solid border-gray-300
                                    rounded
                                    transition
                                    ease-in-out
                                    m-0
                                    focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none" aria-label="Default select example" onChange={(event) => productName = event.target.value}>
                            <option selected> Click To Select A Product</option>
                            {prod}
                        </select>
                    </div>
                </div>
                <div class="">
                    <div class="mb-3 xl:w-70">
                        Sales Person
                        <select class="form-select appearance-none
                                    block
                                    w-full
                                    px-3
                                    py-1.5
                                    text-base
                                    font-normal
                                    text-gray-700
                                    bg-white bg-clip-padding bg-no-repeat
                                    border border-solid border-gray-300
                                    rounded
                                    transition
                                    ease-in-out
                                    m-0
                                    focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none" aria-label="Default select example" onChange={(event) => salesPerson = event.target.value}>
                            <option selected> Click To Select A Sales Person</option>
                            {sale}
                        </select>
                    </div>
                </div>
                <div class="">
                    <div class="mb-3 xl:w-70">
                        Customer
                        <select class="form-select appearance-none
                                    block
                                    w-full
                                    px-3
                                    py-1.5
                                    text-base
                                    font-normal
                                    text-gray-700
                                    bg-white bg-clip-padding bg-no-repeat
                                    border border-solid border-gray-300
                                    rounded
                                    transition
                                    ease-in-out
                                    m-0
                                    focus:text-gray-700 focus:bg-white focus:border-blue-600 focus:outline-none" aria-label="Default select example" onChange={(event) => salesPerson = event.target.value}>
                            <option selected> Click To Select A Customer</option>
                            {sale}
                        </select>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default NewSale;
