import React, {useState} from "react";
import { useSelector } from 'react-redux';
import EditProduct from "../page/editproductpage";
import ProModalEdit from "./editproductmodal";



function ProdcutTable() {
    var productIndex = 0;
    const products = useSelector((state) => state.products);
    console.log(products);

	let proRow = products.map((pro) => {
		var rowColor;

		if (productIndex++ % 2 === 0) {
			rowColor = "dark:bg-stone-700";
		} else {
			rowColor = "dark:bg-stone-600";
		}
		return (
			<tr className={`bg-white ${rowColor} `}>
				<th
					scope="row"
					className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap"
				>
					{pro.name}
				</th>
				<th scope ="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{pro.manufacturer}
				</th>
				<th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{pro.style}
				</th>
				<th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{pro.purchasePrice}
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{pro.salePrice}
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{pro.quantity}
				</th>
                <th scope="row" className="px-2 py-4 font-medium text-gray-900 dark:text-white whitespace-nowrap">
					{pro.commisionPercentage}
				</th>
				<td className="px-2 py-4 whitespace-nowrap">
						<button
                            onClick={function(){
                                setCurrentId(pro._id);
                                window.location.replace(`/editproduct/${<EditProduct  
                                key={pro._id}
                                productId={pro._id}
                                name={pro.name}
                                manufacturer={pro.manufacturer}
                                style={pro.style}
                                purchasePrice={pro.purchasePrice}
                                salePrice={pro.salePrice}
                                quantity={pro.quantity}
                                commisionPercentage={pro.commisionPercentage}/>}`)
                            }}
							className="flex-right px-3 py-2 h-full bg-green-600 hover:bg-green-700 text-white transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none rounded-lg "
						>
							Edit
						</button>
				</td>
			</tr>
		);
	});

	return (
		<div className="mt-10 relative  shadow-md sm:rounded-lg h-full overflow-visible">
			<table className="w-full text-sm text-center text-gray-500 dark:text-gray-50">
				<thead className="text-xs text-white-700 uppercase bg-white-50 dark:bg-white-700 dark:text-white-400 dark:bg-stone-800">
					<tr>
						<th scope="col" className="px-2 py-4">
							Name
						</th>
						<th scope="col" className="px-2 py-4">
							Manufacturer
						</th>
						<th scope="col" className="px-2 py-4">
							Style
						</th>
						<th scope="col" className="px-2 py-4">
							Purchase Price
						</th>
						<th scope="col" className="px-2 py-4">
							Sale Price
						</th>
                        <th scope="col" className="px-2 py-4">
							Quantity
						</th>
                        <th scope="col" className="px-2 py-4">
							Commision Percent
						</th>
                        <th scope="col" className="px-2 py-4">
							Options
						</th>
					</tr>
				</thead>
				<tbody>{proRow}</tbody>
			</table>
		</div>
	);
}
export default ProdcutTable;
